mapper.insert:
insert into paopao_order_returns_apply (id, user_id, order_no, 
  type, order_status, why, 
  audit_status, audit_time, audit_why, 
  create_time, update_time, comment
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{orderNo,jdbcType=BIGINT}, 
  #{type,jdbcType=INTEGER}, #{orderStatus,jdbcType=INTEGER}, #{why,jdbcType=VARCHAR}, 
  #{auditStatus,jdbcType=INTEGER}, #{auditTime,jdbcType=TIMESTAMP}, #{auditWhy,jdbcType=VARCHAR}, 
  now(), now(), #{comment,jdbcType=LONGVARCHAR}
  )

mapper.select:
select

from paopao_order_returns_apply


    audit_status=#{auditStatus}


order by create_time desc
limit #{begin}, #{count}
